function toShortNumber(number) {
    const rounder = Math.pow(10, 1);
    const powers = [
      {key: 'Q', value: Math.pow(10, 15)},
      {key: 'T', value: Math.pow(10, 12)},
      {key: 'B', value: Math.pow(10, 9)},
      {key: 'M', value: Math.pow(10, 6)},
      {key: 'K', value: 1000}
    ];
    let key = '';
    for (const power of powers) {
        const reduced = number / power.value;
        for (const power of powers) {
            let reduced = number / power.value;
            reduced = Math.round(reduced * rounder) / rounder;
            if (reduced >= 1) {
                number = reduced;
                key = power.key;
                break;
            }
        }
    }

    return number + key;
}

function calculateBarValues(count, limit) {
    let apiUsageBar = '0%';
    let apiUsagePercent = '';
    let apiUsageValue = `${toShortNumber(count)}  / ∞`;
    if (Number.isFinite(limit) && limit > 0) {
        var percent = Math.min(100, ((count / limit) * 100));
        apiUsageBar = `${percent}%`
        apiUsagePercent = `${percent.toFixed(2)}%`;
        apiUsageValue = `${toShortNumber(count)} / ${toShortNumber(limit)}`;
    }

    return [apiUsageBar, apiUsagePercent, apiUsageValue]
}

